alter table tbl_master_npwp_new add column nitku_digit varchar(255) default '000000' after new_npwp;
alter table tbl_master_npwp_new add column nitku varchar(255) after new_npwp;

UPDATE tbl_master_npwp_new
SET nitku = SUBSTRING(TRIM(SUBSTRING_INDEX(new_npwp, '/', -1)), 1, 16),
    nitku_digit = SUBSTRING(TRIM(SUBSTRING_INDEX(new_npwp, '/', -1)), 17);










